insert  into `guest_book`(`id`,`name`,`contact_way`,`content`,`is_reply`,`reply`,`is_verify`,`create_date`,`reply_date`) values (15,'王鹏飞','15256577542','如何报名？',1,'根据网站联系方式和地址就可以来报名试听了。详见联系我们。QQ咨询：16653162',1,'2017-06-04 15:29:04','2017-07-13 18:50:00'),(21,'张先生','12256423','你好，老师！请问2017年秋季班什么时候开学，学费大概多少钱？',1,'你好，我们起航电气设计201702班，将于2017年9月初开班，请关注网站通知，加入QQ号：16653162 联系咨询。可以先来视听再学习。',1,'2017-07-13 18:47:26','2017-07-13 18:48:59'),(23,'刘同学','QQ：12231213212','你好，老师，我是大二的学生可以参加电气设计学习吗？',1,'刘同学，你好，大二的同学有了一定基础给你如下建议：1.加入你准备考研，可以考虑提前参加电气设计培训，先掌握一门技术有了保障再考研。2.如果你不考研，也可以大三上参加学习。',1,'2017-09-01 23:58:12','2017-09-02 00:01:11'),(24,'14电气 周小欧','','老师你好，我今年（2017.10月份）就开始找工作了，请问开学后跟您学习电气设计对工作有帮助吗，时间来得及吗？谢谢。',1,'周小欧同学，你好。如果你今年毕业，开学以后可以参加我们的培训班，可以一边学习设计，一边找工作，去年就有6位同学边学习边找工作，培训2个月以后都找到了理想的工作，可以参加我们的学习，欢迎。',1,'2017-09-01 23:59:30','2017-09-02 00:02:50');

/*Table structure for table `news` */

